var title_f4_34_4653="Richmond sedation scale";
var content_f4_34_4653=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F57874&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F57874&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Richmond agitation-sedation scale (RASS)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Score",
"       </td>",
"       <td class=\"subtitle1\">",
"        Term",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +4",
"       </td>",
"       <td>",
"        Combative",
"       </td>",
"       <td>",
"        Overtly combative or violent, immediate danger to staff",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +3",
"       </td>",
"       <td>",
"        Very agitated",
"       </td>",
"       <td>",
"        Pulls on or removes tubes or catheters, aggressive behavior toward staff",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +2",
"       </td>",
"       <td>",
"        Agitated",
"       </td>",
"       <td>",
"        Frequent nonpurposeful movement or patient-ventilator dyssynchrony",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +1",
"       </td>",
"       <td>",
"        Restless",
"       </td>",
"       <td>",
"        Anxious or apprehensive but movements not aggressive or vigorous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        Alert and calm",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -1",
"       </td>",
"       <td>",
"        Drowsy",
"       </td>",
"       <td>",
"        Not fully alert, sustained (&gt;10 seconds) awakening, eye contact to voice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -2",
"       </td>",
"       <td>",
"        Light sedation",
"       </td>",
"       <td>",
"        Briefly (&lt;10 seconds) awakens with eye contact to voice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -3",
"       </td>",
"       <td>",
"        Moderate sedation",
"       </td>",
"       <td>",
"        Any movement (but no eye contact) to voice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -4",
"       </td>",
"       <td>",
"        Deep sedation",
"       </td>",
"       <td>",
"        No response to voice, any movement to physical stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -5",
"       </td>",
"       <td>",
"        Unarousable",
"       </td>",
"       <td>",
"        No response to voice or physical stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        Procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1.",
"       </td>",
"       <td colspan=\"2\">",
"        Observe patient. Is patient alert and calm (score 0)?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        2.",
"       </td>",
"       <td colspan=\"2\">",
"        Does patient have behavior that is consistent with restlessness or agitation?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Assign score +1 to +4 using the criteria listed above.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        3.",
"       </td>",
"       <td colspan=\"2\">",
"        If patient is not alert, in a loud speaking voice state patient's name and direct patient to open",
"eyes and look at speaker. Repeat once if necessary. Can prompt patient to continue looking at speaker.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Patient has eye opening and eye contact, which is sustained for more than 10 seconds (score -1).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Patient has eye opening and eye contact, but this is not sustained for 10 seconds (score -2).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Patient has any movement in response to voice, excluding eye contact (score -3).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        4.",
"       </td>",
"       <td colspan=\"2\">",
"        If patient does not respond to voice, physically stimulate patient by shaking shoulder and then rubbing sternum if there is no response.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Patient has any movement to physical stimulation (score -4).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        Patient has no response to voice or physical stimulation (score -5).",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Sessler, C, Gosnell, M, Grap, MJ, et al. The Richmond agitation-sedation scale. Validity and reliability in adult intensive care unit patients. Am J Respir Crit Care Med 2002; 166:1338. Copyright &copy; 2002 American Thoracic Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
