" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology of gastric cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/35/4661/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/35/4661/contributors\">",
"     Annie On On Chan, MD",
