" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Nocturnal leg cramps",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/38/4711/contributors\">",
