var title_f4_39_4734="Retinal hemorrhage etiology";
var content_f4_39_4734=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F81915&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F81915&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of retinal hemorrhage in infants and young children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency of retinal hemorrhages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Hemorrhage description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Abusive head trauma",
"        </strong>",
"       </td>",
"       <td>",
"        50 to 100 percent of cases",
"       </td>",
"       <td>",
"        Numerous, extend to retinal periphery, involve multiple layers of the retina",
"       </td>",
"       <td>",
"        History inconsistent with injuries; commonly associated with subdural hemorrhage, fractures, bruising, and intraabdominal injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Perinatal retinal hemorrhages",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         20 to 30 percent of newborns examined during first 24 hours",
"        </p>",
"        <p>",
"         10 to 15 percent of newborns examined during first 72 hours",
"        </p>",
"       </td>",
"       <td>",
"        May be numerous and extend to retinal periphery; usually intraretinal, but may be preretinal or vitreal",
"       </td>",
"       <td>",
"        <p>",
"         More common in vacuum-assisted deliveries",
"        </p>",
"        <p>",
"         Resolves by 4 to 6 weeks",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Unintentional head injury",
"        </strong>",
"       </td>",
"       <td>",
"        0-10 percent of patients",
"       </td>",
"       <td>",
"        Typically few in number and confined to posterior pole",
"       </td>",
"       <td>",
"        Associated with severe mechanism of injury (eg, motor vehicle crash, fall from great height)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Hematologic conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bleeding disorder (eg, hemophilia, von Willebrand disease, vitamin K deficiency)",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"       <td>",
"        All layers of retina, vitreous",
"       </td>",
"       <td>",
"        Characteristic history of bleeding disorder and abnormal coagulation studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leukemia",
"       </td>",
"       <td>",
"        Unknown; most patients have fundoscopic changes at some point in disease",
"       </td>",
"       <td>",
"        Usually at the posterior pole in the deep layers of the retina, but may occur in all layers and in the vitreous",
"       </td>",
"       <td>",
"        Characteristic laboratory features (eg, lymphoblasts on peripheral blood smear)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anemia (juvenile pernicious, iron deficiency, sickle cell)",
"       </td>",
"       <td>",
"        Unknown, more common in adults than children",
"       </td>",
"       <td>",
"        Intraretinal dot, blot, flame, or splinter",
"       </td>",
"       <td>",
"        Anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Metabolic conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glutaric aciduria type 1",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Few in number and confined to the posterior pole",
"       </td>",
"       <td>",
"        Macrocephaly, characteristic basal ganglia findings, may present with subdural hematomas; absence of bony abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Galactosemia",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Vitreous hemorrhage",
"       </td>",
"       <td>",
"        Failure to thrive, cataracts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cerebral malaria",
"       </td>",
"       <td>",
"        20 to 60 percent of children with severe cerebral malaria",
"       </td>",
"       <td>",
"        Multiple white lesions",
"       </td>",
"       <td>",
"        Travel history; fever, anemia, characteristic blood smear findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Meningitis",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Various",
"       </td>",
"       <td>",
"        Fever, characteristic cerebrospinal fluid findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Retinal infection (eg, CMV, HSV, toxoplasmosis, rickettsiae)",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Small, intraretinal at areas of retinal necrosis; may span all layers of necrotic retina",
"       </td>",
"       <td>",
"        Systemic signs of infection may be present; immunodeficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endocarditis",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        White-centered oval hemorrhages",
"       </td>",
"       <td>",
"        Other characteristic features (eg, cardiac murmur, fever, splinter hemorrhages in the nail beds, Janeway lesions)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Primary retinal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Retinopathy of prematurity",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Small, usually intraretinal on the surface of the neovascular ridge; may extend to the vitreous",
"       </td>",
"       <td>",
"        History of prematurity; retinal neovascularization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coats disease",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Usually intraretinal but may extend to vitreous",
"       </td>",
"       <td>",
"        Usually unilateral; subretinal exudate; telangiectatic vessels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persistent fetal vasculature",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        May present with vitreous hemorrhage",
"       </td>",
"       <td>",
"        Usually unilateral; may be associated with mild microphthalmos; intralenticular hemorrhage; retrolental membrane",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intracranial hemorrhage",
"       </td>",
"       <td>",
"        Rare in absence of abusive head injury",
"       </td>",
"       <td>",
"        May be extensive",
"       </td>",
"       <td>",
"        Characteristic neuroradiographic features (eg, aneurysm, arterio-venous malformation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Chest compression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cardiopulmonary resuscitation",
"       </td>",
"       <td>",
"        Rare if ever",
"       </td>",
"       <td>",
"        Few in number, small in size",
"       </td>",
"       <td>",
"        History of cardiopulmonary resuscitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Thoracic crush injury (Purtscher syndrome)",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Superficial; white retinal patches",
"       </td>",
"       <td>",
"        History of thoracic injury",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CMV: cytomegalovirus; HSV: herpes simplex virus.",
"     <br>",
"      * The presence of an underlying condition does not exclude the possibility of abusive head trauma.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     This appendix is modified from an article entitled, \"A 12-Year Ophthalmologic Experience with the Shaken Baby Syndrome at a Regional Children's Hospital\" in the Trans Am Ophthalmol Soc 1999; 97:545-581 and republished with permission of the American Ophthalmological Society.",
"     <br>",
"      Additional data from: Aryan HE, Ghosheh FR, Jandial R, Levy ML. Retinal hemorrhage and pediatric brain injury: etiology and review of the literature. J Clin Neurosci 2005; 12:624 and Kaur B, Taylor D. Fundus hemorrhages in infancy. Surv Ophthalmol 1992; 37:1.",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
