var content_f4_42_4781=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F78867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F78867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of physical findings in the evaluation of childhood sexual abuse (part 1)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Finding",
"      </td>",
"      <td class=\"subtitle1\">",
"       Differential diagnosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Perineal area (boys or girls)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perineal inflammation or erythema",
"      </td>",
"      <td>",
"       Candidal infection, pinworms, perianal cellulitis, contact dermatitis, atopic dermatitis, psoriasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perineal ulcer or vesicular rash*",
"      </td>",
"      <td>",
"       Syphilis, herpes simplex virus, varicella, chanchroid, Behcet's disease, bullous pemphigoid, Epstein-Barr virus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perineal bruising",
"      </td>",
"      <td>",
"       Mongolian spots, trauma, hemolytic uremic syndrome, Henoch-Schonlein purpura",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Perianal area (boys or girls)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perianal erythema",
"      </td>",
"      <td>",
"       Encopresis, poor hygiene, pinworms, Group A streptococcal or staphylococcal infection, irritants, trauma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anal fissures",
"      </td>",
"      <td>",
"       Constipation; perianal irritation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Perianal scar",
"      </td>",
"      <td>",
"       May be caused by medical conditions, such as fissures, or lesions of Crohn's disease, or from previous medical procedures",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Venous congestion or pooling",
"      </td>",
"      <td>",
"       Usually due to positioning of child; may be seen in constipation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Flattened anal folds",
"      </td>",
"      <td>",
"       May be caused by relaxation of the anal sphincter or swelling of the perianal tissues from infection or trauma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anogenital warts&bull;",
"      </td>",
"      <td>",
"       Human papillomavirus, molluscum contagiousum, common warts, skin tags",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Rectal bleeding",
"      </td>",
"      <td>",
"       Hemorrhoids, Crohn's disease, rectal prolapse, rectal tumors; anal trauma or penetration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anal dilation to less than 2 cm, with or without stool present",
"      </td>",
"      <td>",
"       May be a normal reflex or caused by severe constipation or encopresis, sedation, anesthesia, neuromuscular conditions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"       Male genitalia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Penile trauma",
"      </td>",
"      <td>",
"       <p>",
"        Hair tourniquet, zipper entrapment injury, straddle injury",
"       </p>",
"       <p>",
"        (history is usually readily available for zipper entrapment and",
"       </p>",
"       <p>",
"        straddle injuries; straddle injuries usually affect the anterior",
"       </p>",
"       <p>",
"        structures)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Penile or scrotal erythema",
"      </td>",
"      <td>",
"       Irritants, infection, trauma",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Cultures for herpes simplex virus type 1 and 2 and syphilis should be obtained.",
"     <br>",
"      <br>",
"       &bull; May need biopsy to confirm diagnosis.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Bays, J, Jenny, C. Genital and",
"     <br>",
"      anal conditions confused with child sexual abuse trauma. Am J Dis Child",
"      <br>",
"       1990; 144:1319. Hymel, KP, Jenny, C. Child sexual abuse. Pediatr Rev",
"       <br>",
"        1996; 17:236. Adams, JA. Evolution of a classification scale: medical",
"        <br>",
"         evaluation of suspected child sexual abuse. Child Maltreat 2001; 6:31.",
"         <br>",
"          Adams, JA. Medical evaluation of suspected child sexual abuse. J",
"          <br>",
"           Pediatr Adolesc Gynecol 2004; 17:191.",
"          </br>",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
