var title_f4_44_4812="Rx options cardiogenic shock";
var content_f4_44_4812=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F82406&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F82406&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment options for cardiogenic shock due to left ventricular dysfunction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       General measures",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ventilation support to correct hypoxemia and, in part, acidosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Optimize intravascular volume",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sodium bicarbonate only for severe metabolic acidosis (arterial pH less than 7.10 to 7.15)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Aspirin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intravenous heparin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Possible glycoprotein IIb/IIIa inhibitor with NSTEMI",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Insertion of pulmonary artery catheter",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Specific measures",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Pharmacologic support",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Sympathomimetic inotropes (eg, dopamine)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Norepinephrine (for refractory hypotension)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Mechanical support",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       IABP, usually combined with percutaneous coronary intervention or coronary artery bypass graft surgery or possible thrombolytic therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       Newer devices",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Left ventricular or biventricular assist devices",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Percutaneous cardiopulmonary bypass",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Reperfusion/revascularization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Primary percutaneous coronary intervention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Coronary artery bypass graft",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Thrombolytic therapy for patients not receiving PCI in a timely manner",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
