var title_f4_44_4813="Common long-term and late effects of breast cancer treatment";
var content_f4_44_4813=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F81721&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F81721&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common long-term and late effects of breast cancer treatment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management options",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Surgical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cosmetic effects",
"       </td>",
"       <td>",
"        Plastic surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Functional disability of arm or chest wall, pain",
"       </td>",
"       <td>",
"        Physical therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Scarring/adhesions",
"       </td>",
"       <td>",
"        Plastic surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lymphedema",
"       </td>",
"       <td>",
"        Physical therapy, avoid trauma to involved arm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Radiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Second malignancies",
"       </td>",
"       <td>",
"        Image masses arising near radiation field",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypothyroidism",
"       </td>",
"       <td>",
"        Check TSH if symptoms of hypothyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pneumonitis, pulmonary fibrosis",
"       </td>",
"       <td>",
"        Symptomatic management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cardiac damage",
"       </td>",
"       <td>",
"        Lifestyle risk-reduction (diet, exercise, tobacco avoidance)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lymphedema",
"       </td>",
"       <td>",
"        As above",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Systemic therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Second malignancies (myelodysplasia and leukemia)",
"       </td>",
"       <td>",
"        Check CBC if symptoms of leukemia arise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ototoxicity (eg, cisplatin)",
"       </td>",
"       <td>",
"        Symptomatic management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cardiomyopathy (eg, anthracyclines)",
"       </td>",
"       <td>",
"        Symptomatic management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Renal toxicity (eg, cisplatin)",
"       </td>",
"       <td>",
"        Symptomatic management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Premature menopause and infertility (eg, alkylating agents)",
"       </td>",
"       <td>",
"        Referral to infertility specialist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Menopausal symptoms",
"       </td>",
"       <td>",
"        Gabapentin, SNRI (eg, venlafaxine), SSRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sexual dysfunction",
"       </td>",
"       <td>",
"        Counseling, lubricants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Osteoporosis (eg, hormonal therapy, chemotherapy)",
"       </td>",
"       <td>",
"        Calcium, vitamin D, exercise, bisphosphonate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Neuropathy (eg, taxanes and platinums)",
"       </td>",
"       <td>",
"        Symptomatic management (eg, antidepressants, antiepileptics for painful neuropathy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cognitive dysfunction",
"       </td>",
"       <td>",
"        Cognitive therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Weight gain",
"       </td>",
"       <td>",
"        Exercise and diet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fatigue",
"       </td>",
"       <td>",
"        Exercise, rule out other causes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CBC: complete blood count; SNRI: serotonin-norepinephrine reuptake inhibitors; SSRI: selective serotonin reuptake inhibitors; TSH: thyroid stimulating hormone.",
"    </div>",
"    <div class=\"reference\">",
"     Source: Hayes DF. Clinical practice. Follow-up of patients with early breast cancer. N Engl J Med 2007; 356:2505.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
