var title_f4_45_4829="Minor heat illness treatment in children";
var content_f4_45_4829=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F70997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F70997&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation and management of minor heat illness in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Condition",
"      </td>",
"      <td class=\"subtitle1\">",
"       Core body temperature",
"      </td>",
"      <td class=\"subtitle1\">",
"       Clinical findings",
"      </td>",
"      <td class=\"subtitle1\">",
"       Treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Miliaria (heat rash)",
"      </td>",
"      <td>",
"       Normal",
"      </td>",
"      <td>",
"       Pruritic erythematus or skin colored papular, vesicular, or pustular rash",
"      </td>",
"      <td>",
"       <p>",
"        Light clothing to decrease sweating",
"       </p>",
"       <p>",
"        Symptomatic treatment of pruritus",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heat edema",
"      </td>",
"      <td>",
"       Normal",
"      </td>",
"      <td>",
"       Swelling of feet, ankles, and/or lower legs",
"      </td>",
"      <td>",
"       <p>",
"        Cool environment*",
"       </p>",
"       <p>",
"        Elevation of legs",
"       </p>",
"       <p>",
"        Compression stockings",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heat syncope",
"      </td>",
"      <td>",
"       Normal",
"      </td>",
"      <td>",
"       Dizziness, orthostatic hypotension, and syncope after exertion with rapid return to normal mental status when supine",
"      </td>",
"      <td>",
"       <p>",
"        Move to cool environment*",
"       </p>",
"       <p>",
"        Supine position",
"       </p>",
"       <p>",
"        Oral rehydration with salt-containing fluids&bull; or intravenous normal saline (initial infusion 20 mL per kg)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heat cramps",
"      </td>",
"      <td>",
"       Normal or elevated up to 40&deg;C (104&deg;F)",
"      </td>",
"      <td>",
"       Painful cramps of large muscle groups in the legs (eg, quadriceps, gastrocnemius) occurring after exertion",
"      </td>",
"      <td>",
"       <p>",
"        Passively stretch affected muscle",
"       </p>",
"       <p>",
"        Oral rehydration with salt-containing fluids&bull; or intravenous normal saline (initial infusion 20 mL per kg, maximum initial volume: 1 L)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heat tetany",
"      </td>",
"      <td>",
"       Normal or elevated up to 40&deg;C (104&deg;F)",
"      </td>",
"      <td>",
"       Hyperventilation with carpopedal spasm, paresthesias",
"      </td>",
"      <td>",
"       <p>",
"        Move to cool environment*",
"       </p>",
"       <p>",
"        If symptoms persistent, low flow (&lt;5 L per min.) oxygen via a partial rebreathing mask to encourage rebreathing of CO2",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"3\">",
"       Heat exhaustion",
"      </td>",
"      <td rowspan=\"3\">",
"       Elevated up to 40&deg;C (104&deg;F)",
"      </td>",
"      <td rowspan=\"3\">",
"       <p>",
"        Normal mental status, dizziness, brief episode of syncope, or mild confusion that rapidly normalizes within 30 minutes of treatment",
"       </p>",
"       <p>",
"        Tachycardia with normal blood pressure and mild to moderate dehydration",
"       </p>",
"       <p>",
"        Nausea, vomiting, headache, fatigue, weakness, thirst, sweating, or, in some patients, hyponatremia or hypernatremia",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Cease exertion immediately",
"       </p>",
"       <p>",
"        Move to cool environment*",
"       </p>",
"       <p>",
"        Remove excess clothing",
"       </p>",
"       <p>",
"        Oral rehydration with cool, salt-containing liquids&bull;",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       If symptoms continue despite above measures or if altered mental status:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       <p>",
"        - Measure rectal temperature",
"       </p>",
"       <p>",
"        - Measure serum electrolytes",
"       </p>",
"       <p>",
"        - Administer IV normal saline (initial infusion 20 mL per kg, maximum initial volume: 1 L)",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Cool environments include natural shade, an air conditioned vehicle, or air conditioned building.",
"     <br>",
"      &bull; Flavored sports drinks (eg, Gatorade&reg;) may be used.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      1. Howe, AS, Boden, BP. Heat-related illness in athletes. Am J Sports Med 2007; 35:1384.",
"      <br>",
"       2. DeFranco, MJ, Baker CL, 3rd, DaSilva, JJ, et al. Environmental issues for team physicians. Am J Sports Med 2008; 36:2226.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
