" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidermolysis bullosa acquisita",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/47/4858/contributors\">",
