var title_f4_47_4860="Pretreatment for patients with prior RCM reactions";
var content_f4_47_4860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F59096&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F59096&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pretreatment for patients with previous adverse reactions to radiocontrast media",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Non-urgent pretreatment:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Glucocorticoid-preferred:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adult: Oral prednisone 50 mg at 13, 7, and 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pediatric: Prednisone 0.5 to 0.7 mg/kg oral (maximum 50 mg per dose) at 13, 7, and 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Glucocorticoid-alternate: (oral administration not possible)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adult: IV methylprednisolone 40 mg at 13, 7, and 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pediatric: Methylprednisolone 0.5 mg/kg IV (maximum 40 mg per dose) at 13, 7, and 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         AND",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        H1 antihistamine:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adult: Diphenhydramine 50 mg oral, IM, or IV 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pediatric: Diphenhydramine 1.25 mg/kg oral, IM, or IV (maximum 50 mg) 1 hour prior to procedure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Emergency pretreatment:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methylprednisolone 40 mg IV every 4 hours until contrast study required plus diphenhydramine 50 mg IV 1 hour prior to contrast injection (pediatric doses as above).*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Lower/iso-osmolar RCM should be recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Emergency therapy for a recurrent hypersensitivity reaction should be available",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RCM: radiocontrast media.",
"     <br/>",
"     * Glucocorticoids have not been shown to be effective when given less than 4 to 6 hours prior to contrast injection.",
"    </div>",
"    <div class=\"reference\">",
"     American College of Radiology, \"Manual on Contrast Media, Version 8.0\", &copy; 2012.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
