" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Physical assessment to identify signs of a difficult airway in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Difficulty",
"       </td>",
"       <td class=\"subtitle1\">",
"        Signs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positioning",
"       </td>",
"       <td>",
"        Prominent or misshapen occiput, short neck, poor neck mobility",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bag-mask ventilation",
"       </td>",
"       <td>",
"        Facial anomalies, facial trauma (including burns)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laryngoscopy",
"       </td>",
"       <td>",
"        Small mouth, small mandible, abnormal palate, large tongue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intubation",
"       </td>",
"       <td>",
"        Signs of upper airway obstruction (hoarseness, stridor, drooling, upright position of comfort)",
"       </td>",
"      </tr>",
"     </tbody>",
