var title_f4_53_4956="Pt selection laser surgery";
var content_f4_53_4956=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F72987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F72987&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Patient selection criteria for laser refractive surgery",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Age 18 years or older with appropriate expectations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stable refraction at least one year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myopia between -.50 and -12.00 diopters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Astigmatism &le;5.00 diopters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperopia &lt;+6.00 diopters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Absence of ocular contraindications:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Keratoconus, pellucid marginal degeneration, keratoglobus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Herpetic keratitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Progressive myopia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Corneal disease*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Glaucoma*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cataract*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Any other pre-existing pathology of the cornea or anterior segment, including scarring, lagophthalmos, severe dry eye and blepharitis*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Absence of medical contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Uncontrolled vascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Autoimmune disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Immunosuppressed/immunocompromised",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pregnant or nursing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        History of keloids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Diabetes mellitus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absence of use of systemic medication such as isotretinoin, amiodarone",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Determined by ophthalmologist.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
