" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prenatal sonographic diagnosis of fetal cardiac anomalies",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Joshua Copel, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Louise Wilkins-Haug, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Deborah Levine, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/54/4968/contributors\">",
"     Deputy Editor",
"    </a>",
