var title_f4_55_4990="CF infection control A";
var content_f4_55_4990=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F53301%7EPULM%2F63038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F53301%7EPULM%2F63038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of infection control recommendations for healthcare settings for patients with cystic fibrosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Setting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Category*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"9\">",
"        General principles",
"       </td>",
"       <td>",
"        Assume all patients with CF could have transmissible",
"pathogens and apply Standard precautions",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Implement transmission-based precautions according to",
"CDC/HICPAC published recommendations",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Healthcare workers should use approved methods of hand",
"hygiene",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gloves should be worn when caring for patients who require Contact or Droplet precautions",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Published recommendations for sterilization and",
"disinfection of patient care equipment should be followed",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gowns should be worn as defined by Standard or pathogen",
"specific precautions",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wall humidifiers and \"in-line\" and hand-held nebulizers",
"should be cleaned and dried according to manufacturer's",
"recommendations; disposable and single-use items should be discarded",
"after use",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In ambulatory care settings, examination room surfaces should",
"be cleaned after the room is vacated; regular cleaning of",
"other surfaces should be on a regular basis and as soiling occurs",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PFT",
"equipment should use disposable in-line bacterial filters in between each patient; disposable mouthpieces are",
"preferred; sterilization of the internal machinery of PFT machines is",
"not needed between patients",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"4\">",
"        Microbiology and surveillance",
"       </td>",
"       <td>",
"        Molecular typing using approved genotyping methods",
"should be performed as epidemiologically indicated",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Respiratory tract cultures should be performed at least",
"quarterly and processed for culture and susceptibility according",
"to CF-specific guidelines",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        All Burkholderia cepacia complex isolates should be",
"confirmed and speciated at the Cystic Fibrosis Foundation Burkholderia",
"cepacia Research Laboratory and Repository (University of Michigan)",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Surveillance strategies should be developed in",
"collaboration with the institutional infection control team",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CF: cystic fibrosis; HICPAC: Hospital Infection Control Practices Advisory Committee; MRSA: methicillin-resistant staphylococcus aureus; PFT: pulmonary function testing; VRE: vancomycin-resistant enterococcus.",
"     <br>",
"      * Categories are based on the CDC/HICPAC system. Category IA: strongly recommended for implementation and strongly supported by well designed experimental, clinical, or epidemiologic studies. Category IB: strongly recommended for implementation and supported by some experimental, clinical, or epidemiologic studies and a strong theoretic rationale. Category II: suggested for implementation and supported by suggestive clinical or epidemiologic studies or a theoretic rationale.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Gibson, RL, Burns, JL, Ramsey, BW. Pathophysiology and management of pulmonary infections in cystic fibrosis. Am J Respir Crit Care Med 2003; 168:918. Copyright &copy; 2003 American Thoracic Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of infection control recommendations for healthcare settings for patients with cystic fibrosis (continued)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Setting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Category*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"7\">",
"        Ambulatory",
"       </td>",
"       <td>",
"        Encourage hand hygiene and have waterless antiseptics",
"or other products available for use by patients",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Observe Contact plus Standard precautions for",
"epidemiologically important pathogens (eg, B. cepacia complex, MRSA,",
"multidrug-resistant Pseudomonas aeruginosa)",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Develop a reliable method for tracking patients' most",
"recent culture and susceptibility testing results",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alert other diagnostic areas of patients' transmission",
"precautions&nbsp;",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Segregate patients infected with B. cepacia complex,",
"and",
"place patients with multidrug-resistant P. aeruginosa in room",
"immediately",
"       </td>",
"       <td>",
"        IB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Manage scheduling to minimize time in common waiting",
"areas",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discourage",
"use of common items in waiting area that cannot be cleaned between",
"patients (eg, toys, computer)",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Inpatient",
"       </td>",
"       <td>",
"        All patients with CF with B. cepacia complex, MRSA, or",
"VRE",
"should be housed in single-patient rooms that do not share common",
"facilities (eg, bathroom, shower)&bull;",
"       </td>",
"       <td>",
"        IA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with CF, and without the above organisms may",
"share rooms with patients without CF who are low risk for infection&bull;",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients not on transmission-based precautions may be",
"evaluated for activity outside their hospital room as long as they are",
"educated according to hand hygiene and avoidance of direct contact with",
"other patients with CF, and with appropriate disinfection of surfaces.",
"       </td>",
"       <td>",
"        II",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CF: cystic fibrosis; HICPAC: Hospital Infection Control Practices Advisory Committee; MRSA: methicillin-resistant staphylococcus aureus; PFT: pulmonary function testing; VRE: vancomycin-resistant enterococcus.",
"     <br>",
"      * Categories are based on the CDC/HICPAC system. Category IA: strongly recommended for implementation and strongly supported by well designed experimental, clinical, or epidemiologic studies. Category IB: strongly recommended for implementation and supported by some experimental, clinical, or epidemiologic studies and a strong theoretic rationale. Category II: suggested for implementation and supported by suggestive clinical or epidemiologic studies or a theoretic rationale.",
"      <br>",
"       &bull; Patients with CF who sleep in the same room at home may share a hospital room (Category II).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Gibson, RL, Burns, JL, Ramsey, BW. Pathophysiology and management of pulmonary infections in cystic fibrosis. Am J Respir Crit Care Med 2003; 168:918. Copyright &copy; 2003 American Thoracic Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
