" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Commonly used therapies for myasthenia gravis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Time to onset of effect*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Time to maximal effect*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Symptomatic therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pyridostigmine",
"       </td>",
"       <td>",
"        10 to 15 minutes",
"       </td>",
"       <td>",
"        2 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Chronic immunotherapies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prednisone",
"       </td>",
"       <td>",
"        2 to 3 weeks",
"       </td>",
"       <td>",
"        5 to 6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azathioprine",
"       </td>",
"       <td>",
"        ~12 months",
"       </td>",
"       <td>",
"        1 to 2 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycophenolate mofetil",
"       </td>",
"       <td>",
"        6 to 12 months",
"       </td>",
"       <td>",
"        1 to 2 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclosporine",
"       </td>",
"       <td>",
"        ~6 months",
"       </td>",
"       <td>",
"        ~12 months",
"       </td>",
"      </tr>",
"      <tr>",
