var title_f4_57_5020="Perpetrator characteristics Munchausen by proxy";
var content_f4_57_5020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F52225&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F52225&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Usual Munchausen syndrome by proxy perpetrator characteristics (not to be considered a profile)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Usually the mother",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presents initially as a \"normal\" or \"good\" caretaker",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Accomplished liar, deceiver, and manipulator; extremely believable,",
"convincing, and superb in ability to give plausible explanation for his",
"or her behavior",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knows what he or she is doing; not simply overanxious or overprotective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May or may not have extensive health care knowledge",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deny all or part of the maltreatment they have perpetrated, even when",
"there is extensive evidence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May change behavior when they are suspected or caught, but do not",
"usually stop it",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May or may not add or change health care providers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May or may not have mental health pathology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May have a personal history of symptom/illness falsification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Appears to have good, or even excellent, perpetrator-victim dynamics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Often have no prior child protection agency involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Should be considered more dangerous once they believe they are suspected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use their victims to satisfy their internal needs through the attention",
"they receive from having a child with problems; their needs are much",
"more important",
"to them than the needs of the child; external gain may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May seek attention from professionals and nonprofessionals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May change their",
"maltreatment methods",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Lasher, LJ. MBP overview and definitions. file://www.mbpexpert.com/definition.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
