var title_f4_60_5068="Cytology of follicular thyroid lesions";
var content_f4_60_5068=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F70424&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F70424&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cytopathologic diagnosis of follicular thyroid lesions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cytologic pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Histopathology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Benign macrofollicular fragments",
"       </td>",
"       <td>",
"        <p>",
"         Abundant colloid",
"        </p>",
"        <p>",
"         Predominantly large follicles and flat sheets of follicular cells",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Nodular goiter (colloid adenoma)",
"        </p>",
"        <p>",
"         Macrofollicular adenoma",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Follicular lesion or atypia of undetermined significance",
"       </td>",
"       <td>",
"        &nbsp;",
"        <p>",
"         Mixed macrofollicular and microfollicular nodules",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Cells with mild nuclear atypia",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Extensive oncocytic (H&uuml;rthle cell) change",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"        <p>",
"         Multinodular goiter",
"        </p>",
"        <p>",
"         Follicular adenoma",
"        </p>",
"        <p>",
"         Follicular cancer",
"        </p>",
"        <p>",
"         Papillary cancer (follicular variant)",
"        </p>",
"        <p>",
"         Hashimoto's thyroiditis",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <p>",
"         Follicular neoplasm",
"        </p>",
"        <p>",
"         (microfollicular)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Scant colloid",
"        </p>",
"        <p>",
"         Small microfollicles",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Clusters and clumps of cells with varying pleomorphism",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Microfollicular adenoma",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Follicular carcinoma",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cells have intranuclear inclusions and clefts",
"       </td>",
"       <td>",
"        Follicular variant of papillary carcinoma",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
