var title_f4_8_4237="Evaluation bronchiectasis 2";
var content_f4_8_4237=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F69598&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F69598&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of bronchiectasis-2",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Specific examples/features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Immunodeficiency states",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IgG deficiency",
"       </td>",
"       <td>",
"        Congenital (Bruton's type) agammaglobulinemia; selective deficiency of subclasses (IgG2, IgG4); acquired immune globulin deficiency; common variable hypogammaglobulinemia; Nezelof's syndrome; \"bare lymphocyte\" syndrome",
"       </td>",
"       <td>",
"        Quantitative immunoglobulin levels; immunoglobulin subclass levels; impaired response to immunization with pneumococcal vaccine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IgA deficiency",
"       </td>",
"       <td>",
"        Selective IgA deficiency &plusmn; ataxia-telegiectasia syndrome",
"       </td>",
"       <td>",
"        Quantitative immunoglobulin levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leukocyte dysfunction",
"       </td>",
"       <td>",
"        Chronic granulomatous disease (NADPH oxidase dysfunction)",
"       </td>",
"       <td>",
"        Dihydrorhodamine 123 (DHR) oxidation test; nitroblue tetrazolium test; genetic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Other rare humoral immunodeficiencies (CXCR4 mutation, CD40 deficiency, CD40 ligand deficiency, and others)",
"       </td>",
"       <td>",
"        WHIM; Hypergammaglobulinemia M",
"       </td>",
"       <td>",
"        Neutrophil count; quantitative immunoglobulin levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Abnormal secretion clearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ciliary defects of airway mucosa",
"       </td>",
"       <td>",
"        Kartagener's syndrome; ciliary dyskinesis",
"       </td>",
"       <td>",
"        Chest image showing situs inversus; bronchial biopsy; ciliary motility studies; electron microscopy of sperm or respiratory mucosa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cystic fibrosis (mucoviscidosis)",
"       </td>",
"       <td>",
"        Typical early childhood syndrome; later presentation with predominantly sinopulmonary symptoms",
"       </td>",
"       <td>",
"        Sweat chloride; genetic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Young's syndrome",
"       </td>",
"       <td>",
"        Obstructive azoospermia with sinopulmonary infections",
"       </td>",
"       <td>",
"        Sperm count",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Miscellaneous disorders",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alpha-1 antitrypsin deficiency",
"       </td>",
"       <td>",
"        Absent or abnormal antitrypsin synthesis and function",
"       </td>",
"       <td>",
"        Alpha-1 antitrypsin level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent aspiration pneumonia",
"       </td>",
"       <td>",
"        Alcoholism; neurologic disorders; lipoid pneumonia",
"       </td>",
"       <td>",
"        History; chest imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rheumatic&nbsp;disease",
"       </td>",
"       <td>",
"        Associated with rheumatoid arthritis and Sjogren's syndrome",
"       </td>",
"       <td>",
"        Rheumatoid factor; antiSSA/antiSSB; salivary gland MRI or biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inflammatory bowel disease",
"       </td>",
"       <td>",
"        Crohn's disease; ulcerative colitis",
"       </td>",
"       <td>",
"        History; lower gastrointestinal endoscopy; imaging studies; colonic biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inhalation of toxic fumes and dusts",
"       </td>",
"       <td>",
"        Ammonia; nitrogen dioxide, or other irritant gases; smoke; talc; silicates",
"       </td>",
"       <td>",
"        Exposure history; chest imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic rejection following organ transplantation",
"       </td>",
"       <td>",
"        Bone marrow, lung and heart lung transplantation; associated with obliterative bronchiolitis",
"       </td>",
"       <td>",
"        History; PFT; chest CT imaging with inspiratory and expiratory views",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
