var title_f40_10_41131="Common x-ray views by fracture location";
var content_f40_10_41131=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F66310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F66310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common radiographic views for identifying fractures by location",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Anatomic region",
"       </td>",
"       <td class=\"subtitle1\">",
"        Plain radiograph fracture views",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wrist",
"       </td>",
"       <td>",
"        <p>",
"         AP, lateral",
"        </p>",
"        <p>",
"         Oblique: fracture suspected but AP and lateral negative*",
"        </p>",
"        Scaphoid: scaphoid fracture suspected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elbow",
"       </td>",
"       <td>",
"        AP, lateral, oblique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shoulder",
"       </td>",
"       <td>",
"        AP, scapular Y, axillary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Knee",
"       </td>",
"       <td>",
"        <p>",
"         AP, lateral, oblique",
"         <sup>",
"          &bull;",
"         </sup>",
"         (internal or external rotated)",
"        </p>",
"        Sunrise (axial, tangential): patellar injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Foot",
"       </td>",
"       <td>",
"        <p>",
"         AP, lateral",
"        </p>",
"        Oblique: if fracture seen on AP or lateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tibia, femur, forearm",
"       </td>",
"       <td>",
"        AP, lateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ankle",
"       </td>",
"       <td>",
"        AP, lateral, and mortise",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AP: anterior-posterior.",
"     <br/>",
"     * Provides view of the scaphoid-trapezoid-trapezium articulation.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Provides a different projection of the femoral condyles and tibial tuberosities as well as a cleaner view of the medial and lateral margins of the patella.",
"     <br/>",
"     &Delta; Mortise view requires 10 to 20 degrees of internal rotation and allows the tibia and fibula to be viewed without superimposition.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
