"     <div class=\"reference\">",
"      Chamberlain TM, Cali TS, Cuzzell J, et al, &ldquo;Assessment and Management of Pressure Sores in Long-Term Care Facilities,&rdquo;",
"      <i>",
"       Consult Pharm",
"      </i>",
"      , 1992, 7(12)1328-40.",
"     </div>",
"    </li>",
"   </ol>",
"  </div>",
