" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Systemic treatment of metastatic soft tissue sarcoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Suzanne George, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     George D Demetri, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Robert Maki, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/11/41146/contributors\">",
"     Diane MF Savarese, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?40/11/41146/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
