var title_f40_11_41147="Empirical dosing of emergency antidotes";
var content_f40_11_41147=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F66622&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F66622&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empirical dosing of emergency antidotes to adult patients in extremis*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Suspected toxin (or similar agent)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antidote",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyanide",
"       </td>",
"       <td>",
"        5 g hydroxocobalamin IV, repeat x 1 (alternative is nitrite/thiosulfate kit)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methemoglobinemia",
"       </td>",
"       <td>",
"        2 mL/kg 2 percent methylene blue IV over 5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Digoxin",
"       </td>",
"       <td>",
"        2 to 5 vials digoxin antibody (Fab) fragments IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium channel blocker",
"       </td>",
"       <td>",
"        20 mL 10 percent calcium chloride IV, repeat x 2; 0.5 to 1 units/kg/hour insulin (and dextrose) IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Local anesthetic",
"       </td>",
"       <td>",
"        1.5 mL/kg of 20 percent intralipid bolus IV, may repeat several times (alternatively, after bolus infuse 0.25 mL/kg per minute for 30 minutes; maximum dose 10 mL/kg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sodium channel blockade",
"       </td>",
"       <td>",
"        100 mL of 7.5 percent to 8.4 percent sodium bicarbonate IV, repeat x 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cholinergic agent",
"       </td>",
"       <td>",
"        2 mg atropine IV , doubled every 3 minutes as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isoniazid",
"       </td>",
"       <td>",
"        5 g pyridoxine over 10 minutes or until seizures stop; remainder over 4 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chloroquine",
"       </td>",
"       <td>",
"        2 mg/kg diazepam IV over 30 minutes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Additional details about antidotes and further treatment of the poisons listed in this table can be found in the specific reviews discussing these poisonings.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
