" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of the treatment of chronic myeloid leukemia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Robert S Negrin, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Charles A Schiffer, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Richard A Larson, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/16/41225/contributors\">",
"     Rebecca F Connor, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
