var title_f40_16_41229="Pharmacologic treatment of high altitude illness";
var content_f40_16_41229=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F76719&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F76719&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pharmacologic treatment of high altitude illness",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"10%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preferred agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        <strong>",
"         AMS/HACE",
"        </strong>",
"       </td>",
"       <td>",
"        Prevention*",
"       </td>",
"       <td>",
"        <strong>",
"         Acetazolamide:",
"        </strong>",
"        125 mg PO every 12 hrs",
"        <br/>",
"        <p>",
"         Children: 2.5 mg/kg (maximum single dose: 125 mg) PO every 12 hrs",
"        </p>",
"        <p>",
"         Duration: start day before ascent and continue 2-3 days at maximum altitude; may use once at night thereafter to improve sleep",
"        </p>",
"       </td>",
"       <td>",
"        <strong>",
"         Dexamethasone:",
"        </strong>",
"        2 mg PO every 6 hrs or 4 mg PO every 12 hrs",
"        <br/>",
"        <p>",
"         Children: Acetazolamide preferred, do not use for prophylaxis",
"        </p>",
"        <p>",
"         Duration: start day of ascent and continue 2-3 days at maximum altitude, but for no more than 7 days total",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment mild AMS",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        <strong>",
"         Acetazolamide:",
"        </strong>",
"        125 to 250 mg PO every 12 hours",
"        <br/>",
"        <p>",
"         Children: 2.5 mg/kg (maximum single dose: 250 mg) PO every 12 hours",
"        </p>",
"        <p>",
"         Duration: continue for 24 hrs after symptoms resolve or descent accomplished",
"        </p>",
"       </td>",
"       <td>",
"        <strong>",
"         Dexamethasone:",
"        </strong>",
"        2 to 4 mg PO every 6 hrs",
"        <br/>",
"        <p>",
"         Children: 0.15 mg/kg PO every 6 hrs (maximum single dose: 4 mg)",
"        </p>",
"        <p>",
"         Duration: continue until 24 hrs after symptoms resolve or descent completed, but not longer than 7 days total",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment moderate to severe AMS",
"       </td>",
"       <td>",
"        <strong>",
"         Dexamethasone:",
"        </strong>",
"        4 mg PO every 6 hrs",
"        <br/>",
"        <p>",
"         Children: 0.15 mg/kg PO (maximum single dose: 4 mg) every 6 hrs",
"        </p>",
"        <p>",
"         Duration: continue until 24 hrs after symptoms resolve or descent completed, but not longer than 7 days total",
"        </p>",
"       </td>",
"       <td>",
"        <strong>",
"         Acetazolamide:",
"        </strong>",
"        125 to 250 mg PO every 12 hours",
"        <br/>",
"        <p>",
"         Children: 2.5 mg/kg (maximum single dose: 250 mg) PO every 12 hours",
"        </p>",
"        <p>",
"         Duration: continue for 24 hrs after symptoms resolve or descent accomplished",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HACE treatment",
"       </td>",
"       <td>",
"        <strong>",
"         Dexamethasone:",
"        </strong>",
"        8 mg PO/IM/IV once, then 4 mg PO/IM/IV every 6 hours",
"        <br/>",
"        <p>",
"         Children: 0.15 mg/kg every 6 hours (maximum single dose: 4 mg)",
"        </p>",
"        <p>",
"         Duration: continue until 24 hrs after symptoms resolve or descent completed, but not longer than 7 days total",
"        </p>",
"       </td>",
"       <td>",
"        <strong>",
"         Acetazolamide:",
"        </strong>",
"        250 mg PO every 12 hours, may use as adjunct with dexamethasone; NOT for monotherapy",
"        <br/>",
"        <p>",
"         Children: 2.5 mg/kg (maximum single dose: 250 mg) PO every 12 hours",
"        </p>",
"        <p>",
"         Duration: continue until 24 hrs after symptoms resolve or descent accomplished",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         HAPE",
"        </strong>",
"       </td>",
"       <td>",
"        HAPE prevention*",
"       </td>",
"       <td>",
"        <strong>",
"         Nifedipine:",
"        </strong>",
"        60 mg extended-release PO divided daily (30 mg PO every 12 hrs; or 20 mg PO every 8 hrs)",
"        <br/>",
"        <p>",
"         Children: 0.5 mg/kg (maximum single dose: 20 mg) extended-release PO every 8 hrs",
"        </p>",
"        <p>",
"         Duration: start day before ascent and continue for 3-5 days at max altitude",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Further research is needed before the medications listed below can be recommended for routine use:",
"        </p>",
"        <p>",
"         <strong>",
"          Tadalafil:",
"         </strong>",
"         10 mg PO every 12 hrs; start day of ascent and continue 3-5 days at maximum altitude",
"        </p>",
"        <p>",
"         <strong>",
"          Sildenafil:",
"         </strong>",
"         50 mg PO every 8 hours; start day of ascent and continue 3-5 days at maximum altitude",
"        </p>",
"        <p>",
"         <strong>",
"          Dexamethasone:",
"         </strong>",
"         8 mg PO every 12 hours; start day of ascent and continue 48-72 hours at maximum altitude",
"        </p>",
"        <p>",
"         <strong>",
"          Acetazolamide:",
"         </strong>",
"         125-250 mg PO every 12 hours; start day before ascent and continue 48-72 hours at maximum altitude",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HAPE treatment",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        <strong>",
"         Nifedipine:",
"        </strong>",
"        60 mg extended-release PO divided daily (30 mg PO every 12 hrs; or 20 mg PO every 8 hrs)",
"        <br/>",
"        <p>",
"         Children: 0.5 mg/kg (maximum single dose: 20 mg) extended-release PO every 8 hrs",
"        </p>",
"        <p>",
"         Duration: continue until descent completed, symptoms resolved and SpO2 normal for altitude",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Further research is needed before the medications listed below can be recommended for routine use in HAPE treatment:",
"        </p>",
"        <p>",
"         <strong>",
"          Tadalafil:",
"         </strong>",
"         10 mg PO every 12 hours",
"        </p>",
"        <p>",
"         <strong>",
"          Sildenafil:",
"         </strong>",
"         50 mg PO every 8 hrs",
"        </p>",
"        <p>",
"         Duration: continue until descent completed, symptoms resolved and SpO2 normal for altitude",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * See text. Gradual ascent profile is primary method of prevention. Reserve pharmacologic prophylaxis for mod to high-risk scenarios.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     See text. May not require any pharmacologic intervention. Rest, halt ascent, and symptomatic treatment may be sufficient.",
"     <br/>",
"     &Delta; See text. May not require any pharmacologic intervention. In proper setting, rest and supplemental oxygen may be sufficient.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Luks A, McIntosh S, Grissom CK, et al. Wilderness Medical Society Consensus Guidelines for the Prevention and Treatment of Acute Altitude Illness. Wilderness Environ Med 2010; 21:146.",
"      </li>",
"      <li>",
"       Pollard A, Niermeyer S, Barry P, et al. Children at High Altitude: An International Consensus, Statement by an Ad Hoc Committee of the International Society for Mountain Medicine, March 12, 2001. High Altitude Medicine &amp; Biology. Volume 2, Number 3, 2001. Mary Ann Liebert, Inc. Pages 389-403.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
