var title_f40_22_41324="Ketamine intoxication - Rapid overview";
var content_f40_22_41324=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F69157&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F69157&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ketamine intoxication: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical presentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired consciousness is the most common presentation, although ketamine intoxication may cause a range of central neurologic symptoms, from mild agitation to hallucinations; mild tachycardia and hypertension often occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Massive overdose can cause coma or apnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laryngospasm and heavy salivation may infrequently occur during intravenous use, even with standard doses; laryngospasm is rare but occurs most often in infants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vertical or rotatory nystagmus may occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diagnosis of ketamine intoxication is based on history and clinical evidence; no readily available definitive laboratory test exists",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain fingerstick glucose, acetaminophen and salicylate concentrations, electrocardiogram, and pregnancy test in women of childbearing age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Secure airway, breathing, and circulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Laryngospasm and respiratory depression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Generally resolve with noninvasive support (eg, bag-mask ventilation; oxygen); endotracheal intubation is rarely required",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Salivation that compromises respirations or interferes with procedures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Treat with",
"        <strong>",
"         atropine",
"        </strong>",
"        (0.01 to 0.02 mg/kg, minimum dose 0.1 mg, may be repeated every five minutes) or",
"        <strong>",
"         glycopyrrolate",
"        </strong>",
"        (5 mcg/kg, may be repeated once after five minutes)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Psychomotor agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Treat with",
"        <strong>",
"         benzodiazepines",
"        </strong>",
"        (eg, intravenous doses of",
"        <strong>",
"         diazepam",
"        </strong>",
"        0.1 mg/kg or in adults 5 to 10 mg, or",
"        <strong>",
"         lorazepam",
"        </strong>",
"        0.05 mg/kg or in adults 1 to 2 mg) until the desired level of sedation is achieved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Butyrophenones (eg, haloperidol, droperidol) and other antipsychotic agents",
"        <strong>",
"         should not be used",
"        </strong>",
"        to treat agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Disposition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with uncomplicated ketamine toxicity may be observed and discharged when symptoms have resolved",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
