"      <td bgcolor=\"#eeeeee\" width=\"1%\">",
"       <br/>",
"      </td>",
"      <td align=\"left\" bgcolor=\"#eeeeee\">",
"       <span class=\"medCalcFontTitleBox\">",
"        Calculator: International Prostatism Symptom Score (IPSS)",
"       </span>",
"      </td>",
"     </tr>",
"    </table>",
"    <br/>",
"    &nbsp;",
"    <br/>",
"    <center>",
"     <table cellpadding=\"3\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\">",
"      <tr>",
"       <td align=\"left\" colspan=\"3\">",
"        <span class=\"medCalcFontCCTabBold\">",
"         Over the past month, how often have you had a sensation of not emptying your bladder completely after you finished urinating?",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td width=\"20\">",
"        <br/>",
"       </td>",
"       <td align=\"right\">",
"        <input name=\"cc1\" onclick=\"IPSS_fx();\" type=\"radio\"/>",
"       </td>",
"       <td align=\"left\">",
"        <span class=\"medCalcFontCCTab\">",
"         Not at all (0 points)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td width=\"20\">",
"        <br/>",
"       </td>",
"       <td align=\"right\">",
"        <input name=\"cc1\" onclick=\"IPSS_fx();\" type=\"radio\"/>",
"       </td>",
"       <td align=\"left\">",
"        <span class=\"medCalcFontCCTab\">",
"         Less than 1 time in 5 (1 point)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td width=\"20\">",
"        <br/>",
"       </td>",
