var title_f40_33_41499="Ophthalmalogic dilating agents abusive head trauma";
var content_f40_33_41499=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F57408&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F57408&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dilating drops recommended for fundus examination for nonaccidental head injury (shaken baby syndrome)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Name",
"      </td>",
"      <td class=\"subtitle1\">",
"       Efficacy/advantages",
"      </td>",
"      <td class=\"subtitle1\">",
"       Duration of large pupil&bull;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Disadvantages",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tropicamide 1/2 or 1 percent",
"      </td>",
"      <td>",
"       Good in less pigmented eyes",
"      </td>",
"      <td>",
"       4 to 6 hours",
"      </td>",
"      <td>",
"       Works less well in brown eyes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cyclopentolate 1/2 or 1 percent",
"      </td>",
"      <td>",
"       Good in all eyes",
"      </td>",
"      <td>",
"       1 to 2 days",
"      </td>",
"      <td>",
"       Long duration 2 percent solution can cause hallucinations",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Phenylephrine 2.5 percent",
"      </td>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       <p>",
"        Poor by itself",
"       </p>",
"       <p>",
"        Very helpful combined with tropicamide or cyclopentolate",
"       </p>",
"      </td>",
"      <td>",
"       3 to 4 hours",
"      </td>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       <p>",
"        Systemic hypertension in higher strengths",
"       </p>",
"       <p>",
"        Used alone, pupil still constricts during examination",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cyclomydril&reg; (0.2 percent cyclopentolate and 1 percent phenylephrine; Alcon laboratories)",
"      </td>",
"      <td colspan=\"1\" rowspan=\"1\">",
"       <p>",
"        Good in less pigmented eyes",
"       </p>",
"       <p>",
"        Commonly used for ROP examinations",
"       </p>",
"       <p>",
"        Convenient combination drop",
"       </p>",
"      </td>",
"      <td>",
"       3 to 4 hours",
"      </td>",
"      <td>",
"       Often needs to be repeated for darker eyes",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ROP: retinopathy of prematurity.",
"     <br>",
"      &bull; Repeat dosage will prolong duration by up to 50 percent.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Kivlin, JD. Ophthalmic manifestations of shaken baby syndrome. Journal of Aggression, Maltreatment &amp; Trauma 2001; 5:137. &copy;2001 The Haworth Press, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
