" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of lactotroph adenoma (prolactinoma) during pregnancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Peter J Snyder, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     David S Cooper, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Charles J Lockwood, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/34/41510/contributors\">",
"     Kathryn A Martin, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
