"     <tr>",
"      <td>",
"       Mycobacterial infections outside the chest",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Fungal airway colonization",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hepatitis B core antibody",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Herpesviruses (HHV 6-8, simplex, varicella)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cytomegalovirus",
"      </td>",
"     </tr>",
