var title_f40_34_41516="Dx criteria atopic dermatitis";
var content_f40_34_41516=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F57187&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F57187&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for atopic dermatitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Major criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pruritus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dermatitis affecting flexural surfaces in adults and the face and extensors in infants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic or relapsing dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Personal or family history of cutaneous or respiratory atopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Minor criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Features of the so-called \"atopic facies\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Facial pallor or erythema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypopigmented patches",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Infraorbital darkening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Infraorbital folds or wrinkles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cheilitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Recurrent conjunctivitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anterior neck folds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Triggers of atopic dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Foods",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Emotional factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Environmental factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Skin irritants such as wool, solvents and sweat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Complications of atopic dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Susceptibility to cutaneous viral and bacterial infections",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Impaired cell-mediated immunity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Immediate skin-test reactivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Raised serum IgE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Keratoconus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anterior subcapsular cataracts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Others",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Early age of onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dry skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ichthyosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hyperlinear palms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Keratosis pilaris (plugged hair follicles of proximal extremities)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hand and foot dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nipple eczema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        White dermatographism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Perifollicular accentuation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Diagnosis requires the presence of at least three major and three minor criteria.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Hanifin JM, Rajka G, Acta Dermatol Venereol 1980; 92(Suppl):44.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
