var title_f40_36_41549="Causes of chronic wrist injury in children and adolescents";
var content_f40_36_41549=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63434&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63434&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of chronic wrist injury in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Location",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated activities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distal radius physis stress syndrome",
"       </td>",
"       <td>",
"        Radial",
"       </td>",
"       <td>",
"        Pain with dorsiflexion and axial loading",
"       </td>",
"       <td>",
"        Widening and irregularity of the physis; palmar spurring of metaphysis",
"       </td>",
"       <td>",
"        Gymnastics; rock climbing; weight lifting;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Median neuropathy (Carpal tunnel syndrome)",
"       </td>",
"       <td>",
"        Radial",
"       </td>",
"       <td>",
"        Dysesthesia or paresthesias; positive Tinels or Phelan sign",
"       </td>",
"       <td>",
"        Carpal tunnel view to exclude osseous compression",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports; cycling; rowing; skiing; keyboard typing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radial neuropathy (Wartenburg syndrome)",
"       </td>",
"       <td>",
"        Radial",
"       </td>",
"       <td>",
"        Pain and paresthesias over the dorsal thumb and dorsal radial hand",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Activities that involve repeated supination and pronation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scaphoid impaction",
"       </td>",
"       <td>",
"        Radial",
"       </td>",
"       <td>",
"        Pain over the scapholunate ligament when wrist is dorsiflexed and axially loaded",
"       </td>",
"       <td>",
"        Normal or may demonstrate ossicle formation",
"       </td>",
"       <td>",
"        Gymnastics; weight lifting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        de Quervain tenosynovitis",
"       </td>",
"       <td>",
"        Radial",
"       </td>",
"       <td>",
"        Pain during pinch grasping or thumb and wrist movement",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports; fly fishing; repetitive thumb extension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dorsal ganglion",
"       </td>",
"       <td>",
"        Radial, dorsal",
"       </td>",
"       <td>",
"        Cystic swelling",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kienbock disease(Osteonecrosis of the lunate)",
"       </td>",
"       <td>",
"        Radial, dorsal",
"       </td>",
"       <td>",
"        Mild swelling, persistent pain, crepitation, stiffness and decreased range of motion",
"       </td>",
"       <td>",
"        AP radiographs demonstrate progressive changes",
"       </td>",
"       <td>",
"        Martial arts; volleyball",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ulnar neuropathy (Guyon canal syndrome)",
"       </td>",
"       <td>",
"        Ulnar",
"       </td>",
"       <td>",
"        Weakness and paresthesias affecting the 4th and 5th digits",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports, cycling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ulnar impaction syndrome",
"       </td>",
"       <td>",
"        Ulnar",
"       </td>",
"       <td>",
"        Tenderness of ulnar snuff box; tenderness with ulnar deviation",
"       </td>",
"       <td>",
"        Positive ulnar variance, cyst formation or sclerosis of ulnar head or lunotriquetral interface",
"       </td>",
"       <td>",
"        Gymnastics (vaulting)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Triangular fibrocartilage complex (TFCC) tears",
"       </td>",
"       <td>",
"        Ulnar, dorsal",
"       </td>",
"       <td>",
"        Point tenderness over the lateral wrist just distal to the ulnar styloid; pain with forced ulnar deviation or TFCC compression test",
"       </td>",
"       <td>",
"        Associated (but not diagnostic) findings: widening of DRUJ space, positive ulnar variance, fracture of ulnar styloid on AP; dislocation/subluxation on lateral",
"       </td>",
"       <td>",
"        Impact sports; racquet, stick, and club sports; gymnastics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distal radioulnar joint (DRUJ) injury",
"       </td>",
"       <td>",
"        Ulnar, dorsal",
"       </td>",
"       <td>",
"        Pain with supination and pronation; positive DRUJ compression or piano key tests",
"       </td>",
"       <td>",
"        Dislocation can be seen on plain radiographs; MRI may be necessary for instability",
"       </td>",
"       <td>",
"        Impact sports; racquet, stick, and club sports; gymnastics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extensor carpi ulnaris (ECU) tendonitis",
"       </td>",
"       <td>",
"        Ulnar, dorsal",
"       </td>",
"       <td>",
"        Tenderness over ECU tendon in ulnar groove exacerbated by wrist extension against resistance",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Rowing; racquet, stick, and club sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ECU subluxation",
"       </td>",
"       <td>",
"        Ulnar, dorsal",
"       </td>",
"       <td>",
"        Pain and snapping with rotation of forearm; can be observed with ulnar deviation in full supination",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hamate hook fracture",
"       </td>",
"       <td>",
"        Ulnar, palmar",
"       </td>",
"       <td>",
"        Pain with sports but not daily activities; point tenderness over the hook of the hamate",
"       </td>",
"       <td>",
"        Fracture best seen on 45&deg; supination oblique or carpal tunnel view or with computed tomography",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flexor tendonitis (flexor carpi radialis [FCR], fexor carpi ulnaris [FCU])",
"       </td>",
"       <td>",
"        Palmar",
"       </td>",
"       <td>",
"        Tenderness over FCR or FCU",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Racquet, stick, and club sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypothenar hammer syndrome",
"       </td>",
"       <td>",
"        Palmar",
"       </td>",
"       <td>",
"        Pain in palm, coldness of digits, abnormal modified Allen test",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Impact sports; martial arts; volleyball; racquet, stick, and club sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intersection syndrome",
"       </td>",
"       <td>",
"        Dorsal",
"       </td>",
"       <td>",
"        Tenderness 4 to 6 cm proximal to wrist joint; crepitus with active extension of the wrist",
"       </td>",
"       <td>",
"        Radiographs not necessary",
"       </td>",
"       <td>",
"        Rowing; weight training; racquet, stick, and club sports",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
