" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations, diagnosis, and staging of exocrine pancreatic cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/40/41610/contributors\">",
"     Author",
"    </a>",
