var content_f40_40_41611=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F57339&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F57339&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Miscarriage rate after amniocentesis in series utilizing ultrasound guidance during the procedure",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\" rowspan=\"2\">",
"       Author",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"2\">",
"       Spontaneous abortions",
"      </td>",
"      <td class=\"subtitle1\" rowspan=\"2\">",
"       Difference in rates (95% confidence interval)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       Cases (numbers)",
"      </td>",
"      <td class=\"subtitle2\">",
"       Controls (number)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tabor (1986)",
"      </td>",
"      <td>",
"       1.7% (38/2242)",
"      </td>",
"      <td>",
"       0.7% (17/2270)",
"      </td>",
"      <td>",
"       0.9% (0.3%; 1.5%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Andreasen (1989)",
"      </td>",
"      <td>",
"       2.3% (30/1318)",
"      </td>",
"      <td>",
"       1.4% (4/276)",
"      </td>",
"      <td>",
"       0.8% (-1.3%; 1.9%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tongsong (1998)",
"      </td>",
"      <td>",
"       1.8% (36/2045)",
"      </td>",
"      <td>",
"       1.4% (29/2045)",
"      </td>",
"      <td>",
"       0.3% (-0.4%; 1.1%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antsaklis (2000)",
"      </td>",
"      <td>",
"       2.1% (79/3696)",
"      </td>",
"      <td>",
"       1.5% (80/5324)",
"      </td>",
"      <td>",
"       0.6% (0.1%; 1.2%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Eddleman (2006)",
"      </td>",
"      <td>",
"       1.0% (31/3096)",
"      </td>",
"      <td>",
"       0.9% (300/31,907)",
"      </td>",
"      <td>",
"       0.1% (-0.3%; 0.5%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Kong (2006)",
"      </td>",
"      <td>",
"       1.7% (58/3465)",
"      </td>",
"      <td>",
"       0.8% (9/1125)",
"      </td>",
"      <td>",
"       0.9% (0.1%; 1.4%)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Odibo (2008)",
"      </td>",
"      <td>",
"       0.97% (113/11,695)",
"      </td>",
"      <td>",
"       0.84% (335/39,594)",
"      </td>",
"      <td>",
"       0.13% (-0.07%; 0.20%)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    An increase in rate of spontaneous abortion compared with controls was seen in all study groups following amniocentesis, although the difference did not reach significance in four series.",
"    <div class=\"footnotes\">",
"     %: percent.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
