var title_f40_41_41627="Signs of autism";
var content_f40_41_41627=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63709&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63709&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Signs of autism spectrum disorders",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A child with an ASD might:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not play \"pretend\" games (pretend to feed a doll)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not point at objects to show interest (point at an airplane flying over)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not look at objects when another person points at them",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have trouble relating to others or not have an interest in other people at all",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid eye contact and want to be alone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prefer not to be held or cuddled or might cuddle only when they want to",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Appear to be unaware when other people talk to them but respond to other sounds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Be very interested in people, but not know how to talk to, play with, or relate to them",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Repeat or echo words or phrases said to them, or repeat words or phrases in place of normal language (echolalia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have trouble expressing their needs using typical words or motions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Repeat actions over and over again",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have trouble adapting when a routine changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have unusual reactions to the way things smell, taste, look, feel, or sound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lose skills they once had (for instance, stop saying words they were once using)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Centers for Disease Control and Prevention. National Center on Birth Defects and Developmental Disabilities. \"Learn the signs. Act early.\" campaign. Available at: www.cdc.gov/ncbddd/autism/actearly/autism.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
