var title_f40_42_41643="Lung preservation procurement";
var content_f40_42_41643=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F66453&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F66453&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Donor lung preservation during procurement",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         Ventilation",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Tidal volume: 8 to 10 mL per kg",
"        </p>",
"        <p>",
"         FIO2: 50 percent",
"        </p>",
"        <p>",
"         PEEP: 5 cm H2O",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Anticoagulation (once lungs and heart exposed)",
"        </strong>",
"       </td>",
"       <td>",
"        Heparin 250 to 300 units per kg, intravenously",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Prostaglandin pretreatment",
"        </strong>",
"       </td>",
"       <td>",
"        Prostaglandin E1 (PGE1, Alprostadil) 500 mcg via main pulmonary artery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Flush solution",
"        </strong>",
"       </td>",
"       <td>",
"        Low potassium dextran (Perfadex&reg;)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Temperature",
"        </strong>",
"       </td>",
"       <td>",
"        4 to 8&deg;C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Antegrade flush",
"        </strong>",
"       </td>",
"       <td>",
"        Infuse 50 to 75 mL/kg of flush solution into main pulmonary artery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Cardioplegia solution (only when heart is used for transplantation)",
"        </strong>",
"       </td>",
"       <td>",
"        Infuse retrograde from aorta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Topical cooling",
"        </strong>",
"       </td>",
"       <td>",
"        Iced saline slush added to pleural and pericardial spaces",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Retrograde flush, after removal of heart",
"        </strong>",
"       </td>",
"       <td>",
"        Infuse 250 mL of flush solution into each pulmonary vein orifice and vent at pulmonary artery opening",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
