" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Insulin secretion and pancreatic beta-cell function",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/46/41702/contributors\">",
