var title_f40_48_41739="Rx warfarin associated ICH";
var content_f40_48_41739=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F79151&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F79151&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Reversing anticoagulation in warfarin-associated intracerebral hemorrhage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Management option",
"       </td>",
"       <td class=\"subtitle1\">",
"        Time to anticoagulation reversal",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments and cautions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discontinuing warfarin therapy",
"       </td>",
"       <td>",
"        5 to 14 days",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin K*",
"       </td>",
"       <td>",
"        6 to 24 hours to correct the INR",
"       </td>",
"       <td>",
"        Replacement of factors IX and X takes longer than 24 hours; risk of anaphylaxis with intravenous injection; warfarin resistance lasting up to 1 week may occur after large doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fresh frozen plasma",
"       </td>",
"       <td>",
"        3 to 6 hours for infusion, typically 12 to 32 hours for reversal",
"       </td>",
"       <td>",
"        Volume (2 to 4 L to normalize INR) can be prohibitive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prothrombin complex concentrate",
"       </td>",
"       <td>",
"        15 minutes after 10-minute to 1-hour infusion",
"       </td>",
"       <td>",
"        Limited availability; cost; variable factor VII content based on manufacturer; potentially prothrombotic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Factor VIIa concentrate",
"       </td>",
"       <td>",
"        15 minutes after bolus infusion",
"       </td>",
"       <td>",
"        Short half-life; cost; potentially prothrombotic; uncertain safety. Only replaces factor VII and not the other vitamin K-dependent factors.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     INR: international normalized ratio.",
"     <br/>",
"     * A total of 10 mg intravenously by slow infusion throughout 10 minutes.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Aguilar MI, Hart RG, Kase CS, et al. Treatment of warfarin-associated intercerebral hemorrhage: Literature review and expert opinion. Mayo Clin Proc 2007; 82:82. Copyright &copy; 2007 Dowden Health Media.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
