var title_f40_5_41051="Characteristics of theophylline";
var content_f40_5_41051=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F75856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F75856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Absorption characteristics of selected slow-release formulations of theophylline marketed in the United States",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Formulation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Time to peak serum",
"concentration",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Theo-24",
"       </td>",
"       <td>",
"        Variable depending on whether taken in the morning",
"after an overnight fast, after breakfast, or in the evening.",
"       </td>",
"       <td>",
"        Incomplete absorption occurs when taken after an",
"overnight fast; pH-dependent dissolution causes much more rapid (ie,",
"dose-dumping) and complete absorption when taken after food or in the",
"evening.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Generic SR tablets",
"       </td>",
"       <td>",
"        3 to 7 hours after morning dose when given every 12",
"hours.",
"       </td>",
"       <td>",
"        Scored tablets can be split without affecting",
"absorption characteristics; complete absorption occurs in the presence",
"or absence of food.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Generic SR capsules",
"       </td>",
"       <td>",
"        3 to 7 hours after morning dose when given every 12",
"hours.",
"       </td>",
"       <td>",
"        Can be opened and sprinkled on a spoonful of soft food",
"for children who cannot swallow the capsule; contents must be swallowed",
"without chewing; complete absorption occurs in the presence or absence",
"of food.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uniphyl",
"       </td>",
"       <td>",
"        8 to 12 hours after once-daily evening dose.",
"       </td>",
"       <td>",
"        Incomplete absorption occurs when taken after overnight",
"fast; more complete absorption occurs when taken after food or in the",
"evening.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     The times selected for this table represent the times for a reasonable approximation of the peak serum concentration at the dosing interval most commonly used for the product.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Weinberger, M, Hendeles, L, N Engl J Med 1996; 334:1380.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
