" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of the adult with abdominal pain in the emergency department",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     John L Kendall, MD, FACEP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Maria E Moreira, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Robert S Hockberger, MD, FACEP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?40/50/41770/contributors\">",
"     Jonathan Grayzel, MD, FAAEM",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
