var title_f40_50_41772="Compare UC Crohns";
var content_f40_50_41772=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F74564&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F74564&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of ulcerative colitis and Crohn disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       &nbsp;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Ulcerative colitis",
"      </td>",
"      <td class=\"subtitle1\">",
"       Crohn disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Clinical features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Haematochezia",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"      <td>",
"       Less common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Passage of mucus or pus",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"      <td>",
"       Rare",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Small-bowel disease",
"      </td>",
"      <td>",
"       No (except backwash ileitis)",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Can affect upper-gastrointestinal tract",
"      </td>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Abdominal mass",
"      </td>",
"      <td>",
"       Rare",
"      </td>",
"      <td>",
"       Sometimes in right lower quadrant",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Extraintestinal manifestations",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Small-bowel obstruction",
"      </td>",
"      <td>",
"       Rarely",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Colonic obstruction",
"      </td>",
"      <td>",
"       Rarely",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Fistulas and perianal disease",
"      </td>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Biochemical features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anti-neutrophil cytoplasmic antibodies",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"      <td>",
"       Less common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Anti-saccharomyces cerevisiae antibodies",
"      </td>",
"      <td>",
"       Rarely",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Pathological features",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Transmural mucosal inflammation",
"      </td>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Distorted crypt architecture",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cryptitis and crypt abscesses",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Granulomas",
"      </td>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       Yes (seen in 25 to 40 percent of mucosal biopsies)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Fissures and skip lesions",
"      </td>",
"      <td>",
"       Rarely",
"      </td>",
"      <td>",
"       Common",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This table describes typical features distinguishing between classic ulcerative colitis and Crohn disease. On occasion, patients with ulcerative colitis may have \"nonclassic\" features, including gastritis and mild perianal disease, as discussed separately.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Baumgert, DC, Sandborn, WJ. Inflammatory bowel disease: clinical aspects and established and evolving therapies. Lancet 2007; 369:1641. Copyright &copy;2007 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
