var title_f40_55_41851="Abx therapy pyomyositis";
var content_f40_55_41851=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F77419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F77419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric antimicrobial therapy for pyomyositis in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\">",
"        Regimens for beta-hemolytic streptococci and methicillin-susceptible staphylococci",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cefazolin",
"       </td>",
"       <td>",
"        1 g intravenously every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oxacillin",
"       </td>",
"       <td>",
"        1-2 g intravenously every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nafcillin",
"       </td>",
"       <td>",
"        1-2 g intravenously every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\">",
"        <p>",
"         Regimens for beta-hemolytic streptococci and staphylococci",
"        </p>",
"        <p>",
"         (including activity against MRSA)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Preferred therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vancomycin",
"       </td>",
"       <td>",
"        30 mg/kg intravenously every 24 hours in 2 equally divided doses; not to exceed 2 g/24 hours unless concentrations in serum are inappropriately low",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Alternative agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Daptomycin",
"       </td>",
"       <td>",
"        4 mg/kg intravenously once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Linezolid",
"       </td>",
"       <td>",
"        600 mg intravenously twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ceftraoline",
"       </td>",
"       <td>",
"        600 mg intravenously every 12 hours&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tigecycline",
"       </td>",
"       <td>",
"        100 mg intravenously once, thereafter 50 mg intravenously every 12 hours",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    MRSA: Methicillin-resistant Staphylococcus aureus",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
