var title_f40_56_41867="Chicago protocol for treatment of IVH";
var content_f40_56_41867=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F54351&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F54351&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    University of Illinois at Chicago protocol for intraventricular thrombolysis for IVH",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Exclusions: Unsecured aneurysm or vascular malformation, coagulopathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Monitor ICP continuously",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Withdraw CSF volume equivalent to drug volume to be administered",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       3. Inject t-PA through ventriculostomy:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Dose: 3 to 8 mg t-PA",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Maximum concentration: 4 mg/mL",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Usual dose: 3 to 5 mg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; High dose: 8 mg given for severe IVH with all ventricles affected",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Flush catheter with 1 to 1.5 mL of preservative-free normal saline",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Clamp EVD for 2 hours; unclamp if ICP &gt;25 mm Hg, allowing 1 to 2 mL CSF to drain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. After 2 hours alternate EVD open to zero gravity and EVD closed for one hour at a time for a total of 12 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Follow CT scan at 12 to 24 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       8. Consider repeat t-PA administration at 12 to 24 hours depending on CT scan results",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IVH: intraventricular hemorrhage; ICP: intracranial pressure; CSF: cerebrospinal fluid pressure; EVD: endoventricular drain; CT: computed tomography.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      Andrews, CO, Engelhard, HH. Fibrinolytic therapy in intraventricular hemorrhage. Ann Pharmacother 2001; 35:1435.",
"      <br>",
"       Engelhard, HH, Andrews, CO, Slavin, KV, Charbel, FT. Current management of intraventricular hemorrhage. Surg Neurol 2003; 60:15.",
"      </br>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
