" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Complications of bariatric surgery",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/58/41898/contributors\">",
