var content_f40_58_41899=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F68064&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F68064&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of common headache syndromes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Symptom",
"       </td>",
"       <td class=\"subtitle1\">",
"        Migraine headache",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tension headache",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cluster headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Location",
"       </td>",
"       <td>",
"        Unilateral in 60 to 70 percent; bifrontal or global in 30 percent",
"       </td>",
"       <td>",
"        Bilateral",
"       </td>",
"       <td>",
"        Always unilateral, usually begins around the eye or temple",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Characteristics",
"       </td>",
"       <td>",
"        Gradual in onset, crescendo pattern; pulsating; moderate or severe intensity; aggravated by routine physical activity",
"       </td>",
"       <td>",
"        Pressure or tightness which waxes and wanes",
"       </td>",
"       <td>",
"        Pain begins quickly, reaches a crescendo within minutes; pain is deep, continuous, excruciating, and explosive in quality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient appearance",
"       </td>",
"       <td>",
"        Patient prefers to rest in a dark, quiet room",
"       </td>",
"       <td>",
"        Patient may remain active or may need to rest",
"       </td>",
"       <td>",
"        Patient remains active",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duration",
"       </td>",
"       <td>",
"        4 to 72 hours",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"       <td>",
"        30 minutes to 3 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Associated symptoms",
"       </td>",
"       <td>",
"        Nausea, vomiting, photophobia, phonophobia; may have aura (usually visual, but can involve other senses or cause speech or motor deficits)",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Ipsilateral lacrimation and redness of the eye; stuffy nose; rhinorrhea; pallor; sweating; Horner's syndrome; focal neurologic symptoms rare; sensitivity to alcohol",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
