var title_f40_58_41900="Hormone regimens in the transsexual persons";
var content_f40_58_41900=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F69460&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F69460&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Hormone regimens in the transsexual persons",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        MTF transsexual persons*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <strong>",
"         Estrogen",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Oral: estradiol",
"       </td>",
"       <td>",
"        2.0 to 6.0 mg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Transdermal: estradiol patch",
"       </td>",
"       <td>",
"        0.1 to 0.4 mg twice weekly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\" rowspan=\"2\">",
"        Parenteral: estradiol valerate or cypionate",
"       </td>",
"       <td>",
"        5 to 20 mg im every&nbsp;two weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 to 10 mg im every week",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <strong>",
"         Antiandrogens",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Spironolactone",
"       </td>",
"       <td>",
"        100 to 200 mg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Cyproterone acetate",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        50 to 100 mg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        <strong>",
"         GnRH agonist",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        3.75 mg sc monthly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        FTM transsexual persons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        <strong>",
"         Testosterone",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Oral: testosterone undecanoate",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        160 to 240 mg/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\" colspan=\"2\">",
"        Parenteral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist4_start\">",
"        Testosterone enanthate or cypionate",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        100 to 200 mg im every&nbsp;two weeks or 50 percent weekly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist4_start\">",
"        Testosterone undecanoate",
"        <sup>",
"         &bull;&Delta;",
"        </sup>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        1000 mg every 12 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist3_start\" colspan=\"2\">",
"        Transdermal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist4_start\">",
"        Testosterone gel 1 percent",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        2.5 to 10 g/day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist4_start\">",
"        Testosterone patch",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        2.5 to 7.5 mg/day",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Estrogens used with or without antiandrogens or GnRH agonist.",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      Not available in the United States.",
"      <br>",
"       &Delta; 1000 mg initially, followed by an injection at&nbsp;six weeks, then at 12-week intervals.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Hembree WC, Cohen-Kettenis P, Delemarre-van de Waal HA, et al. Endocrine treatment of transsexual persons: an Endocrine Society clinical practice guideline. J Clin Endocrinol Metab 2009; 94:3132. Copyright &copy; 2009 The Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
