var title_f40_6_41067="Categories of CRS";
var content_f40_6_41067=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F53734&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F53734&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Types of chronic rhinosinusitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Features",
"       </td>",
"       <td class=\"subtitle1\">",
"        AFRS",
"       </td>",
"       <td class=\"subtitle1\">",
"        CRS with NP",
"       </td>",
"       <td class=\"subtitle1\">",
"        CRS without NP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilateral nasal polyps",
"       </td>",
"       <td>",
"        <span class=\"red\" style=\"color: #019e30;\">",
"         Yes (in most cases)",
"        </span>",
"       </td>",
"       <td>",
"        <span class=\"green\">",
"         Yes: Required for diagnosis*",
"        </span>",
"       </td>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         No: Exclusion of polyps is required for diagnosis",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allergic mucin",
"       </td>",
"       <td>",
"        <span style=\"color: #019e30;\">",
"         Yes: Required for diagnosis",
"        </span>",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        <span class=\"green\" style=\"color: #000000;\">",
"         May be present",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aspirin-associated respiratory disease",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        <p>",
"         Asthma present in ~40 percent",
"        </p>",
"        <p>",
"         Aspirin intolerance and asthma present in ~15 percent",
"        </p>",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence of IgE-mediated allergy to fungi",
"       </td>",
"       <td>",
"        <span style=\"color: #019e30;\">",
"         Yes: Required for diagnosis",
"        </span>",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        <span class=\"green\" style=\"color: #000000;\">",
"         May be present",
"        </span>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CRS: chronic rhinosinusitis; NP: nasal polyposis.",
"     <br>",
"      * Medical records documenting removal of bilateral NP during surgery would satisfy this requirement.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
