"     <tr>",
"      <td bgcolor=\"#eeeeee\" width=\"1%\">",
"       <br/>",
"      </td>",
"      <td align=\"left\" bgcolor=\"#eeeeee\">",
"       <span class=\"medCalcFontTitleBox\">",
"        Calculator: CURB-65 pneumonia severity score",
"       </span>",
"      </td>",
"     </tr>",
"    </table>",
"    <br/>",
"    &nbsp;",
"    <br/>",
"    <div id=\"calc_main\">",
"     <div id=\"calc_input\">",
"      <center>",
"       <table cellpadding=\"3\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\">",
"        <tr>",
"         <td width=\"20\">",
"          <br/>",
"         </td>",
"         <td align=\"right\">",
"          <input name=\"cc1\" onclick=\"CURB65_fx();\" type=\"checkbox\"/>",
"         </td>",
"         <td align=\"left\">",
"          <span class=\"medCalcFontCCTab\">",
"           <b>",
"            C",
"           </b>",
"           onfusion",
"           <sup>",
"            *",
"           </sup>",
"           (1 point)",
"          </span>",
"         </td>",
"        </tr>",
"        <tr>",
"         <td width=\"20\">",
"          <br/>",
"         </td>",
"         <td align=\"right\">",
"          <input name=\"cc2\" onclick=\"CURB65_fx();\" type=\"checkbox\"/>",
"         </td>",
"         <td align=\"left\">",
"          <span class=\"medCalcFontCCTab\">",
"           <b>",
"            U",
"           </b>",
"           rea &gt;20 mg/dL (7 mmol/L)",
"           <sup>",
"            **",
"           </sup>",
"           (1 point)",
"          </span>",
"         </td>",
"        </tr>",
"        <tr>",
"         <td width=\"20\">",
"          <br/>",
"         </td>",
"         <td align=\"right\">",
"          <input name=\"cc3\" onclick=\"CURB65_fx();\" type=\"checkbox\"/>",
"         </td>",
"         <td align=\"left\">",
"          <span class=\"medCalcFontCCTab\">",
"           <b>",
"            R",
"           </b>",
"           espiratory rate &gt;=30 breaths per minute (1 point)",
"          </span>",
"         </td>",
"        </tr>",
"        <tr>",
"         <td width=\"20\">",
"          <br/>",
"         </td>",
"         <td align=\"right\">",
"          <input name=\"cc4\" onclick=\"CURB65_fx();\" type=\"checkbox\"/>",
"         </td>",
"         <td align=\"left\">",
"          <span class=\"medCalcFontCCTab\">",
"           Low systolic (&lt;90 mmHg) or diastolic (&lt;=60 mmHg)",
"           <b>",
"            B",
"           </b>",
"           lood pressure (1 point)",
"          </span>",
"         </td>",
"        </tr>",
"        <tr>",
"         <td width=\"20\">",
"          <br/>",
"         </td>",
"         <td align=\"right\">",
"          <input name=\"cc5\" onclick=\"CURB65_fx();\" type=\"checkbox\"/>",
"         </td>",
"         <td align=\"left\">",
"          <span class=\"medCalcFontCCTab\">",
"           Age &gt;=",
"           <b>",
"            65",
"           </b>",
"           years (1 point)",
"          </span>",
"         </td>",
"        </tr>",
"       </table>",
"      </center>",
"     </div>",
"     <br/>",
"     &nbsp;",
"     <br/>",
"     <div id=\"calc_result\">",
"      <span class=\"medCalcFontCCTabBold\">",
"       Total Criteria Point Count:",
"      </span>",
