"function CURB65_fx() {",
"with(document.CURB65_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4.checked){",
"Score = Score + 1;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 1)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 2) && (Score <= 2)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 5)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
