" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of vesicoureteral reflux",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?40/62/41961/contributors\">",
