var title_f40_62_41964="Ampicillin and aminoglycoside resistance in enterococci";
var content_f40_62_41964=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F57470&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F57470&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Mechanisms of ampicillin and aminoglycoside resistance in enterococci",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Resistance",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical MIC (&micro;g/mL)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism or enzyme",
"       </td>",
"       <td class=\"subtitle1\">",
"        Species",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" rowspan=\"2\">",
"        Ampicillin",
"       </td>",
"       <td>",
"        4 - &gt;128",
"       </td>",
"       <td>",
"        &beta;-lactamase production",
"       </td>",
"       <td>",
"        E. faecalis",
"       </td>",
"       <td>",
"        Rare. Specific&nbsp;&beta;-lactamase test is required (eg, nitrocefin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;16",
"       </td>",
"       <td>",
"        Low affinity PBP, increased production of PBP5, or penicillin insensitive transpeptidation",
"       </td>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td>",
"        For isolates with MIC &le;64, high dose ampicillin might be effective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Aminoglycosides*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"8\">",
"        HLR Gentamicin",
"       </td>",
"       <td rowspan=\"2\">",
"        &gt;500",
"       </td>",
"       <td rowspan=\"2\">",
"        Bifunctional enzyme AAC(6')-Ie-APH(2\")-Ia",
"       </td>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td rowspan=\"2\">",
"        Common; streptomycin remains active&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. faecalis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        &gt;500",
"       </td>",
"       <td rowspan=\"2\">",
"        APH(2\")-Id",
"       </td>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td rowspan=\"2\">",
"        Rare; streptomycin remains active&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. casseliflavus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        64 - 256",
"       </td>",
"       <td rowspan=\"3\">",
"        APH(2\")-Ic",
"       </td>",
"       <td>",
"        E. gallinarum",
"       </td>",
"       <td>",
"        It may not be detected by routine screening tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td rowspan=\"2\">",
"        Streptomycin remains active&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. faecalis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &gt;500",
"       </td>",
"       <td>",
"        APH(2\")-Ib",
"       </td>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td>",
"        Streptomycin remains active&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        HLR Streptomycin",
"       </td>",
"       <td rowspan=\"2\">",
"        &gt;1000",
"       </td>",
"       <td rowspan=\"2\">",
"        Adenylyltransferase (ANT), ribosomal mutation",
"       </td>",
"       <td>",
"        E. faecium",
"       </td>",
"       <td rowspan=\"2\">",
"        Gentamicin remains active&Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E. faecalis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HLR: high level resistance; APH: aminoglycoside phosphotransferase; AAC: aminoglycoside acetyltransferase.",
"     <br>",
"      * Screening for HLR to aminoglycosides should be performed on a routine bases when a synergistic effect is required following the NCCLS recommendations [National Committee for Clinical Laboratory Standards. Methods for dilution antimicrobial susceptibility tests for bacteria that grow aerobically: Approved standard. Document M7-A6. NCCLS, Pennsylvania] (see text).",
"      <br>",
"       &bull; Testing for HLR to streptomycin should be performed.",
"       <br>",
"        &Delta; Testing for HLR to gentamicin should be performed.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
